import React, { useRef } from 'react';

const Name = props => {

  const inputRef = useRef();

  const handleSubmit = e => {
    e.preventDefault();
    const name = inputRef.current.value;
    props.onSubmit(name);
    input.current.value = "";
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" ref={inputRef} placeholder="What's your name?"/>
      <input type="submit" />
    </form>
  )
};


export default Name;
