import './App.css';
import Habits from './components/habits.jsx';
import React, { useState } from 'react';

const App = props => {

  const initialHabits = [
    new habitGenerator('Running'),
    new habitGenerator('Eating'),
    new habitGenerator('Reading'),
  ]

  function habitGenerator(habit) {
    this.id = Date.now();
    this.habit = habit;
    this.count = 0;
  }

  const [ habits, setHabits ] = useState(initialHabits);

  const handleIncrement = id => {
    const _habits = habits.map(habit => {
      if (habit.id === id) {
        return (habit.count + 1);
      } return habit;
    });
    setHabits(_habits);
  };

  const handleDecrement = id => {
    const _habits = habits.map(habit => {
      if (habit.id === id) {
        const count = habit.count - 1;
        return count < 0 ? 0 : count;
      } return habit;
    });
    setHabits(_habits);
  };

  const handleDelete = id => {
    const _habits = habits.filter(habit  => habit.id !== id);
    setHabits(_habits);
  };

  return ( 
    <>
      <Habits 
      onIncrement={handleIncrement}
      onDecrement={handleDecrement}
      onDelete={handleDelete}
      habits={habits}
      />
    </>
  )

};

export default App;
