import './App.css';
import Habits from './components/habits.jsx';
import React, { useState } from 'react';
import Header from './components/header.jsx';

const App = props => {

  const initialHabits = [
    new habitGenerator(1, 'Running'),
    new habitGenerator(2, 'Eating'),
    new habitGenerator(3, 'Reading'),
  ]

  function habitGenerator(id, habit) {
    this.id = id;
    this.habit = habit;
    this.count = 0;
  }

  const [ habits, setHabits ] = useState(initialHabits);

  const handleIncrement = id => {
    const _habits = habits.map(habit => {
      if (habit.id === id) {
        return { ...habit, count: habit.count + 1 };
      } return habit;
    });
    setHabits(_habits);
  };

  const handleDecrement = id => {
    const _habits = habits.map(habit => {
      if (habit.id === id) {
        const count = habit.count - 1;
        return { ...habit, count: count < 0 ? 0 : count };
      } return habit;
    });
    setHabits(_habits);
  };

  const handleDelete = id => {
    const _habits = habits.filter(habit => habit.id !== id);
    setHabits(_habits);
  };

  habits.forEach(item => console.log(item));
  // const totalCount = habits.reduce((a.count, b.count) => a.count + b.count);
  return ( 
    <>
      <Header />
      <Habits 
      onIncrement={handleIncrement}
      onDecrement={handleDecrement}
      onDelete={handleDelete}
      habits={habits}
      />
    </>
  )

};

export default App;
